    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        int quant1 = Integer.parseInt(txtQuantMorango.getValue().toString());
        int quant2 = Integer.parseInt(txtQuantComum.getValue().toString());
        int quant3 = Integer.parseInt(txtQuantDoceComum.getValue().toString());
        int quant4 = Integer.parseInt(txtQuantCaramelizados.getValue().toString());
        
        
        double totalMorango = quant1 * 1.5;
        lblTotalMorango.setText(String.format("%.2f", totalMorango));
        
        double totalComum = quant2 * 1.3;
        lblTotalComum.setText(String.format("%.2f", totalComum));
        
        double totalDoceComum = quant3 * 1.0;
        lblTotalDoceComum.setText(String.format("%.2f", totalDoceComum));
        
        double totalCaramelizados = quant4 * 1.3;
        lblTotalCaramelizados.setText(String.format("%.2f", totalCaramelizados));
        
        int totalDoces = quant1 + quant2 + quant3 + quant4;
        lblTotalDoces.setText(Integer.toString(totalDoces));
        
        double total = totalMorango + totalComum + totalDoceComum + totalCaramelizados;
        lblTotal.setText(String.format("%.2f", total));
       
    }//GEN-LAST:event_btnCalcularActionPerformed

